import React, { useState } from 'react';
import {
  RefreshCcw,
  Lightbulb,
  Calendar,
  Briefcase,
  MessageSquare,
  Send,
  Home,
  BarChart
} from 'lucide-react';

export default function Ray() {
  const [inputText, setInputText] = useState('');

  const activities = [
    {
      icon: <RefreshCcw className="w-5 h-5 text-blue-600" />,
      label: 'Request Update',
      path: '/request-update'
    },
    {
      icon: <Lightbulb className="w-5 h-5 text-amber-500" />,
      label: 'Share Suggestion',
      path: '/share-suggestion'
    },
    {
      icon: <Calendar className="w-5 h-5 text-emerald-600" />,
      label: 'Schedule Meeting',
      path: '/schedule-meeting'
    },
    {
      icon: <Briefcase className="w-5 h-5 text-purple-600" />,
      label: 'Internship Referral',
      path: '/internship-referral'
    },
    {
      icon: <MessageSquare className="w-5 h-5 text-rose-600" />,
      label: 'Submit Feedback',
      path: '/submit-feedback'
    }
  ];

  // Navigation section items
  const navItems = [
    { icon: <Home className="w-5 h-5" />, label: 'Home' },
    { icon: <BarChart className="w-5 h-5" />, label: 'Statistics' }
  ];

  return (
    <div className="flex h-screen bg-[#e6f3ff]">
      {/* Left Navigation */}
      <aside className="w-20 bg-white shadow-lg p-4">
        <div className="flex flex-col items-center space-y-6">
          {navItems.map((item, index) => (
            <button
              key={index}
              className="p-3 rounded-xl hover:bg-blue-50 text-gray-600 hover:text-blue-600 transition-colors"
            >
              {item.icon}
            </button>
          ))}
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col p-6 max-w-4xl mx-auto w-full">
        {/* Header with Ray Icon */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center">
            <div className="w-12 h-12 rounded-full bg-gradient-to-br from-purple-400 to-blue-400 flex items-center justify-center text-white font-bold text-lg shadow-lg">
              RAY
            </div>
            <h1 className="ml-4 text-2xl font-bold text-gray-800">Activities</h1>
          </div>
          <div className="flex items-center gap-4">
            <button className="px-4 py-2 bg-white rounded-lg text-blue-600 font-medium hover:bg-blue-50 transition-colors">
              FAQ
            </button>
            <button className="px-4 py-2 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 transition-colors">
              New Worklet
            </button>
          </div>
        </div>

        {/* Search Input */}
        <div className="relative mb-6">
          <input
            type="text"
            placeholder="Type your worklet idea here"
            className="w-full px-4 py-3 bg-white rounded-xl shadow-sm border border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
          />
        </div>

        {/* Activities Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          {activities.map((activity, index) => (
            <button
              key={index}
              className="flex items-center gap-3 p-4 bg-white rounded-xl shadow-sm hover:shadow-md transition-shadow border border-gray-100"
            >
              <div className="p-2 bg-gray-50 rounded-lg">
                {activity.icon}
              </div>
              <span className="font-medium text-gray-700">{activity.label}</span>
            </button>
          ))}
        </div>

        {/* Chat Interface */}
        <div className="flex-1 bg-white rounded-xl shadow-sm border border-gray-200 flex flex-col">
          {/* Chat Messages Area */}
          <div className="flex-1 p-4">
            <div className="flex items-start gap-3 mb-4">
              <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-purple-400 to-blue-400 flex items-center justify-center text-white font-bold text-sm">
                RAY
              </div>
              <div className="bg-gray-100 rounded-lg p-3 max-w-[80%]">
                <p className="text-gray-700">
                  Hello! I'm Ray, your AI assistant. How can I help you today?
                </p>
              </div>
            </div>
          </div>

          {/* Chat Input */}
          <div className="border-t p-4">
            <div className="flex items-center gap-2">
              <input
                type="text"
                placeholder="Type a message..."
                className="flex-1 px-4 py-2 rounded-lg border border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent"
              />
              <button className="p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
                <Send className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
